import API from './client';
import axios from 'axios';

const fetchItemGroups = async () => {
    const res = await axios.get(`http://localhost:4000/api/itemGroups`)
    const itemGroups = res.data;
    console.log(`itemGroups is `,itemGroups);
    return itemGroups;
    /*
    .then(response => {
        console.log(response.data);
        const itemGroups = response.data;
        return itemGroups;
        })
    .catch((err) => {
            throw err.response;
        })
    */

    /*
    try {
        
        const response = await API.get('itemGroups');
        return response.data;
        
    } catch(err) {
        throw err.response.data;
    }
        */
}

const fetchOneItemGroup = async (itemGroupId) => {
    try {
        const response = await API.get(`itemGroups/${itemGroupId}`);
        return response.data;
    } catch(err) {
        throw err.response.data;
    }
}


export default fetchItemGroups;
