import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { addItem } from '../../store/Cart.actions';
import { loadOneItem} from '../../store/Items.actions';
import Button from '@mui/material/Button';

const ItemView = () => {
    const { itemId } = useParams();
    const [ quantity, setQuantity ] = useState(1);
    const dispatch = useDispatch();
    const items = useSelector(state => state.items);
    const cart = useSelector(state => state.cart);
    const item = items[itemId];

    useEffect(() => {
        if(!items[itemId]) {
            (async function load() {
                await dispatch(loadOneItem(itemId))
            })();
        }
    }, [dispatch, items, itemId]);

    async function handleAddToCart() {
        await dispatch(addItem(product, quantity));
    }

    function handleIncrement() {
        setQuantity(quantity + 1);
    }

    function handleDecrement() {
        if ( quantity === 1) {
            return;
        }
        setQuantity (quantity - 1);
    }

    return (
        <div className='ItemView'>
            <img src="" alt={item?.name} />
            <h3>{item?.name}</h3>
            <h4>{item?.description}</h4>
            <h4>{item?.price}</h4>
            <h3>Quantity: {quantity}</h3>
            <Button type="contained" onClick={handleIncrement}>
                +
            </Button>
            <Button type="contained" onClick={handleDecrement}>
                -
            </Button>
            <Button type="contained" onClick={handleAddToCart}>
                Add to cart
            </Button>
        </div>
    );
};

export default ItemView;
