import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { loadItems } from '../../store/Items.actions';
import ItemView from './ItemView';


function Webshop () {
    let { itemGroupId } = useParams();
    const dispatch = useDispatch();
    const items = useSelector(state => state.item);
//    const groupSelector = useSelector(state => state.itemGroup);
//    const itemGroup = groupSelector[itemGroupId];

    useEffect(() => {
        async function load() {
            dispatch (loadItems());
            }
            load();
        },[dispatch]);
    
    console.log('itemGroupId is ',itemGroupId);
    console.log(items);
    var filteredItems = [];
    function filterItems(items, itemGroupId) {
        for (let i=0; i<item.length; i++) {
            if (items[i].itemgroupid === itemGroupId) 
            filteredItems.push(items[i]);
        }
    }

    console.log(`filteredted items is `, filteredItems);

    return (
            <div className='Webshop'>
                { items && 
                Object.keys(items).map((key) => {
                    const item = items[key];
                    return <ItemView data={item} key={item.id} />
                })}
            </div>
            )        
};

export default Webshop;
