import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { loadItems } from '../../store/Items.actions';
import { loadOneItemGroup } from '../../store/ItemGroups.actions';
import Button from '@mui/material/Button';


function Webshop () {
    const { itemGroupId } = useParams();
    const dispatch = useDispatch();
    const items = useSelector(state => state.items);
    const groupSelector = useSelector(state => state.itemGroup);
    const itemGroup = groupSelector[itemGroupId];

    useEffect(() => {
        if(!groupSelector[itemGroupId]) {
        async function load() {
             dispatch(loadOneItemGroup());
        }
        load();
        }
    }, [dispatch, groupSelector, itemGroupId]);

    useEffect(() => {
        async function load() {
            dispatch (loadItems());
            }
            load();
        },[dispatch]);


    return (
        <div className='Webshop'>
            { itemGroup.id === items.itemgroupid && items &&
                <div className="itemListView">
                <p><img src="" alt={items?.name} /></p>
                <p>{items?.name}</p>
                <p>{items?.description}</p>
                <Button type="contained">Add to cart</Button>
                </div>
            }
        </div>
    );
};

export default Webshop;
