import API from './client';
import axios from 'axios';

function fetchItemGroups () {
    axios.get(`http://localhost:4000/api/itemGroups`)
    .then(response => {
        console.log(`we have response: ${response.id}`);
        const resdata = response;
        const itemGroups = resdata.map((itemGroup) => ({
            id: itemGroup.id,
            name: itemGroup.name,
            description: itemGroup.description
            }));
        console.log(`itemGroups after mp: ${itemGroups}`);
        return itemGroups;
        })
    .catch((err) => {
            throw err.response;
        });

        

    /*
    try {
        
        const response = await API.get('itemGroups');
        return response.data;
        
    } catch(err) {
        throw err.response.data;
    }
        */
}

const fetchOneItemGroup = async (itemGroupId) => {
    try {
        const response = await API.get(`itemGroups/${itemGroupId}`);
        return response.data;
    } catch(err) {
        throw err.response.data;
    }
}


export default fetchItemGroups;
