import React, { useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loadItemGroups } from '../../store/ItemGroups.actions' ;
import ItemGroupsPage from './ItemGroups';


function Home() {
    const dispatch = useDispatch();
    const itemGroups = useSelector(state => state.itemGroups);

    useEffect(() => {
        async function load() {
            await dispatch(loadItemGroups());
        }
        load();
    }, [dispatch]);

    return (
        <div className='Home'>
            <h1>Welcome to Emil's Quality Honey and Bee Product Specialities!</h1>
            <h5>(Since 2024!)</h5>
            <h3>Every product is organic and intimately fostered by farmer Emil. Have a look:</h3>
            { Object.keys(itemGroups).map(key => {
                const itemGroup = itemGroups[key];
                return <ItemGroupsPage data={itemGroups} key={itemGroup.id} />
                })
            }
        </div>
    );
};

export default Home;
