import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { loadItems } from '../../store/Items.actions';
//import { loadOneItemGroup } from '../../store/ItemGroups.actions';


function Webshop () {
    const { itemGroupId } = useParams();
    const dispatch = useDispatch();
    const items = useSelector(state => state.item);
//    const groupSelector = useSelector(state => state.itemGroup);
//    const itemGroup = groupSelector[itemGroupId];
    const [ itemList, setItemList] = useState([]);

    useEffect(() => {
        async function load() {
            dispatch (loadItems());
            }
            load();
        },[dispatch]);

    useEffect((items) => {
        setItemList((items) => items.filter((item) => item.itemgroupid === itemGroupId));
    }, []);
    

    console.log(`itemsList is`, itemsList);


    return (
            <div className='Webshop'>

            </div>
            )        
};

export default Webshop;
