import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addCartItem} from '../../store/Cart.actions';
import {  addToCartPersist  } from '../../store/Cart.reducers';
import { checkLogin } from '../../store/Auth.actions';
import Button from '@mui/material/Button';


function ItemView(props) {
    const { data } = props;
    const dispatch = useDispatch();
    const cart = useSelector(state => state.cart);
    const [ quantity, setQuantity ] = useState(1);

    useEffect(() => {
        async function isLoggedIn() {
          dispatch(checkLogin());
        }
        isLoggedIn();
      }, [dispatch]);

    function handleIncrement() {
        setQuantity(quantity + 1);
    }

    function handleDecrement() {
        if ( quantity === 1) {
            return;
        }
        setQuantity (quantity - 1);
    }

    async function handleAddToCart() {
        console.log(`data, quantity dispatch is `, data, quantity);
            if(isLoggedIn) {
                dispatch(addCartItem(data, quantity));
            }
            dispatch(addToCartPersist(data, quantity));
    }

    return (
        <div className='ItemView'>
            <img src="" alt={data.name} />
            <h3>{data.name}</h3>
            <h4>{data.description}</h4>
            <h4>{data.price} â‚¬</h4>
            <h3>Quantity: {quantity}</h3>
            <Button type="contained" onClick={handleIncrement}>
                +
            </Button>
            <Button type="contained" onClick={handleDecrement}>
                -
            </Button>
            <Button type="contained" onClick={handleAddToCart}>
                Add to cart
            </Button>
        </div>
    );
};

export default ItemView;
