import API from './client';
import axios from 'axios';

const fetchItemGroups = async () => {
    axios.get(`http://localhost:4000/api/itemGroups`)
    .then(response => {
        console.log(response.data);
        const itemGroups = {
            id: response.data.id,
            name: response.data.name,
            description: response.data.description
            };
        console.log(`itemGroups after mp: ${itemGroups}`);
        return itemGroups;
        })
    .catch((err) => {
            throw err.response;
        });

        

    /*
    try {
        
        const response = await API.get('itemGroups');
        return response.data;
        
    } catch(err) {
        throw err.response.data;
    }
        */
}

const fetchOneItemGroup = async (itemGroupId) => {
    try {
        const response = await API.get(`itemGroups/${itemGroupId}`);
        return response.data;
    } catch(err) {
        throw err.response.data;
    }
}


export default fetchItemGroups;
