import { createSlice } from '@reduxjs/toolkit';
import { checkLogin } from './Auth.actions';
import { addCartItem, checkOutCart, loadCart, removeCartItem } from './Cart.actions';
/*
const initialState = {
    items: []
};
*/

const cartSlice = createSlice({
    name: 'cart',
    initialState: {
        cart: [],
    },
    reducers: {
        addToCart: (state, action) => {
            const item = action.payload;
            console.log(`action.payload is `, action.payload);
            console.log(`state.cart is `, state.cart);
            console.log(`addtocart item is `, item);
            if(item){
                state.cart = state.cart ? state.cart : state.cart.push(action.payload);
                const isCartItem = state.cart.find((cartItem) => cartItem.id === item.id);
                if(isCartItem) {
                    state.cart.quantity += action.payload.quantity;
                }
                if(!isCartItem) {
                    state.cart.push(action.payload);
                    console.log(`state.cartis after push `, state.cart);
                } 
            }          
        }

    },
    extraReducers: (builder) => {
        builder
            .addCase(addCartItem.fulfilled, (state, action) => {
                const { item } = action.payload;
                state.items.push(item);
            })
            .addCase(checkLogin.fulfilled, (state, action) => {
                const { cart } = action.payload;
                Object.assign(state, cart);
            })
            .addCase(checkOutCart.fulfilled, (state, action) => {
            })
            .addCase(loadCart.fulfilled, (state, action) => {
                const { cart } = action.payload;
                Object.assign(state, cart);
            })
            .addCase(removeCartItem.fulfilled, (state, action) => {
                const { item } = action.payload;
                state.items = state.items.filter((cartItem) => cartItem.id !== item.id);
            })
    }
});


export default cartSlice.reducer;
export const { addToCart } = cartSlice.actions;
