import React, { useCallback, useEffect, useState } from 'react';
import { checkLogin } from '../../store/Auth.actions';
import { useParams } from 'react-router-dom';
import { addCartItem } from '../../store/Cart.actions';
import Button from '@mui/material/Button';


function ItemView(props) {
    const { data } = props;
    const cart = useSelector(state => state.cart);
    const [ quantity, setQuantity ] = useState(1);
    const [ shoppingcart, setShoppingcart ] = useState([]);

    useEffect(() => {
        async function isLoggedIn() {
          await dispatch(checkLogin());
        }
        isLoggedIn();
      }, [dispatch]);

    const addItem = useCallback((cartItem) => {
        if(shoppingcart.some((savedItem) => savedItem.id === cartItem.id))
            return;
        setShoppingcart((prev) => [...prev, cartItem]);
        }, [shoppingcart]);

    function handleIncrement() {
        setQuantity(quantity + 1);
    }

    function handleDecrement() {
        if ( quantity === 1) {
            return;
        }
        setQuantity (quantity - 1);
    }


