import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { loadItems } from '../../store/Items.actions';
import ItemView from './ItemView';


function Webshop () {
    let itemGroupId = useParams();
    const dispatch = useDispatch();
    const items = useSelector(state => state.item);
//    const groupSelector = useSelector(state => state.itemGroup);
//    const itemGroup = groupSelector[itemGroupId];

    useEffect(() => {
        async function load() {
            dispatch (loadItems());
            }
            load();
        },[dispatch]);
    
    filterItems = (items) => {
        var itemList = [...this.state.items]
        var filteredList = itemList.find(function (items) { return items.id === itemGroupid.productId; });
        filteredList = filterItems
        console.log(`filteritems is `, filterItems);
    }
    const itemList = items.filter(item => item.itemgroupid === 2);
    console.log(`itemlist is `, itemList);

    return (
            <div className='Webshop'>
                { items && items.itemgroupid === itemGroupId &&
                Object.keys(items).map((key) => {
                    const item = items[key];
                    return <ItemView data={item} key={item.id} />
                })}
            </div>
            )        
};

export default Webshop;
