import API from './client';
import axios from 'axios';

const fetchItemGroups = async () => {
    axios.get(`http://localhost:4000/api/itemGroups`)
        .then(response => {
            return response.json;
        }).then(data => {
            try {
                return data.itemGroups.map(itemGroup => ({
                    id: itemGroup.id,
                    name: itemGroup.name,
                    description: itemGroup.description
                }))
            } catch(err) {
                throw new Error(err);
            }
        })
        .catch((err) => {
            throw err.response;
        });

        

    /*
    try {
        
        const response = await API.get('itemGroups');
        return response.data;
        
    } catch(err) {
        throw err.response.data;
    }
        */
}

export const fetchOneItemGroup = async (itemGroupId) => {
    try {
        const response = await API.get(`itemGroups/${itemGroupId}`);
        return response.data;
    } catch(err) {
        throw err.response.data;
    }
}


export default fetchItemGroups;
