import React, { useCallback, useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { checkLogin } from '../../store/Auth.actions';
import { useParams } from 'react-router-dom';
import { addCartItem } from '../../store/Cart.actions';
import Button from '@mui/material/Button';


function ItemView(props) {
    const { data } = props;
    const dispatch = useDispatch();
    const cart = useSelector(state => state.cart);
    const [ quantity, setQuantity ] = useState(1);
    const [ shoppingcart, setShoppingcart ] = useState([]);

    const isLoggedIn = useEffect(() => {
        async function isLoggedIn() {
          dispatch(checkLogin());
        }
        isLoggedIn();
      }, [dispatch]);

    const addItem = useCallback((cartItem) => {
        if(shoppingcart.some((item) => item.id === cartItem.id))
            {
            return;
            }
        setShoppingcart((prev) => [...prev, cartItem]);
        }, [shoppingcart]);

    function handleIncrement() {
        setQuantity(quantity + 1);
    }

    function handleDecrement() {
        if ( quantity === 1) {
            return;
        }
        setQuantity (quantity - 1);
    }


    async function handleAddToCartUser() {
        if(isLoggedIn) {
           dispatch(addCartItem(data[id], quantity));
        }
    }

    function handleAddtoCart() {
        addItem(data);
    }

    console.log(`passed props data is `, data);
    console.log(`shoppingCart is `, shoppingcart);

    return (
        <div className='ItemView'>
            <img src="" alt={data.name} />
            <h3>{data.name}</h3>
            <h4>{data.description}</h4>
            <h4>{data.price} â‚¬</h4>
            <h3>Quantity: {quantity}</h3>
            <Button type="contained" onClick={handleIncrement}>
                +
            </Button>
            <Button type="contained" onClick={handleDecrement}>
                -
            </Button>
            { isLoggedIn &&
            <Button type="contained" onClick={handleAddToCartUser}>
                Add to cart
            </Button>
            }
            { !isLoggedIn &&
            <Button type="contained" onClick={handleAddtoCart}>
                Add to cart
            </Button>
            }
        </div>
    );
};

export default ItemView;
