import React, { useEffect } from 'react';
import { useCallback, useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { loadItems } from '../../store/Items.actions';
import ItemView from './ItemView';


function Webshop () {
    let { itemGroupId } = useParams();
    const dispatch = useDispatch();
    const items = useSelector(state => state.item);
    const [shoppingCart, setShoppingCart] = useState([]);

    useEffect(() => {
        async function load() {
            dispatch (loadItems());
            }
            load();
        },[dispatch]);

    const addCartItem = useCallback((newItem) => {
        if(shoppingCart.some((savedItem) => savedItem.id === newItem.id))
            return;
        setShoppingCart((prev) => [...prev, newItem]);
    }, [shoppingCart]);
    
 
    var itemsArray = Object.values(items);
    var filteredItems = [];
    for (let key in itemsArray) {
        if(itemsArray[key].itemgroupid == itemGroupId){
            filteredItems.push(itemsArray[key]);
        }
    }


    return (
            <div className='Webshop'>
                { filteredItems && 
                Object.keys(filteredItems).map((key) => {
                    const item = filteredItems[key];
                    return <ItemView data={item} key={item.id} onAdd={addCartItem} />
                })}
            </div>
            )        
};

export default Webshop;
