import API from './client';
import axios from 'axios';

export const fetchItemGroups = async () => {
    axios.get(`http://localhost:4000/api/itemGroups`)
        .then(response => {
            if(response.data.length){
                const itemGroups = {};
                return itemGroups = response.map(itemGroup => ({
                    id: itemGroup.id,
                    name: itemGroup.name,
                    description: itemGroup.description
                }))
            }
            return console.group(`api response is ${response}`);
        })
        .catch((err) => {
            throw err.response;
        });

        

    /*
    try {
        
        const response = await API.get('itemGroups');
        return response.data;
        
    } catch(err) {
        throw err.response.data;
    }
        */
}

export const fetchOneItemGroup = async (itemGroupId) => {
    try {
        const response = await API.get(`itemGroups/${itemGroupId}`);
        return response.data;
    } catch(err) {
        throw err.response.data;
    }
}


export default fetchItemGroups;
