import React, { useCallback, useEffect, useState } from 'react';
import { checkLogin } from '../../store/Auth.actions';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import Button from '@mui/material/Button';
import CartView from './CartView.js';

function ShoppingCart (props) {
    const { shopping } = props;
    const { items } = useSelector(state => state.cart);
    const dispatch = useDispatch();

    const isLoggedIn = useEffect(() => {
        async function isLoggedIn() {
          dispatch(checkLogin());
        }
        isLoggedIn();
      }, [dispatch]);

    function calcTotal() {
        if(isLoggedIn) {
        return items.reduce((total, { quantity, price }) => {
            return total += quantity * price
        }, 0 )
        }
        return shopping.reduce((total,{ quantity, price }) => {
            return total += quantity * price
        }, 0)
    }

    console.log(`shoppingcart is `, shopping);

    return (
        <div className='ShoppingCart'>
            <p>Your Shopping Bag</p>
            { isLoggedIn && items.map(item => { 
                return (<CartView {...item} />)
                })
            }
            { !isLoggedIn && shopping.map(item => {
                return (<CartView {...item} />)
            })}
        <div className="cartSummary">
            <p>Subtotal {calcTotal()}</p>
            <p>VAT -</p>
            <p>Total {calcTotal()}</p>
        </div>
            <Button
                variant="contained"
                component={Link}
                to="/checkout">
                    Order now!
                </Button>
        </div>
    );
};

export default ShoppingCart;
